var SensorID = "";
var Temperature = 0;
var Humidity = 0;
var LightIntensity = 0;
var NH3PPM = 0;
var NO2PPM = 0;
var COPPM = 0;
var CO2PPM = 0;
var AtmosphericPressure = 0;

SensorID = msg.payload.slice(2, 6);

startIndex = 17;    //where the data starts
increment = 12;     //the spacing between data parts
endIndexNr = 13;    //the value to subtract from the total length to just have sensible data
for(i = startIndex; i < msg.payload.length - endIndexNr; i += increment)
{
    sensorType = msg.payload.substr(i, 2); //size of sensor type data is 2
    readData = msg.payload.substr(i+3, 8); //+3 skips the sensor type, 8 is size of data

    switch(sensorType)
    {
        case "01":
            Temperature = readData;
            break;
        case "02":
            Humidity = readData;
            break;
        case "1a":
            LightIntensity = readData;
            break;
        case "24":
            NH3PPM = readData;
            break;
        case "25":
            NO2PPM = readData;
            break;
        case "26":
            COPPM = readData;
            break;
        case "27":
            CO2PPM = readData;
            break;
        case "28":
            AtmosphericPressure = readData;
            break; 
    }
}

flow.set("SensorID", SensorID);
flow.set("Temperature", Temperature);
flow.set("Humidity", Humidity);
flow.set("LightIntensity", LightIntensity);
flow.set("NH3PPM", NH3PPM);
flow.set("NO2PPM", NO2PPM);
flow.set("COPPM", COPPM);
flow.set("CO2PPM", CO2PPM);
flow.set("AtmosphericPressure", AtmosphericPressure);
return msg;

