var allData =  [flow.get("Temperature"), flow.get("Humidity"), 
                flow.get("LightIntensity"), flow.get("NH3PPM"), 
                flow.get("NO2PPM"), flow.get("COPPM"), flow.get("CO2PPM"), 
                flow.get("AtmosphericPressure")];

for(var j = 0; j < 8; j++)
{
    var currentData = allData[j];
    var newData = 0;

    var int = parseInt(currentData, 16);
    if (int > 0 || int < 0) {
        var sign = (int >>> 31) ? -1 : 1;
        var exp = (int >>> 23 & 0xff) - 127;
        var mantissa = ((int & 0x7fffff) + 0x800000).toString(2);
        var float32 = 0;
        for (i = 0; i < mantissa.length; i += 1) { float32 += parseInt(mantissa[i]) ? Math.pow(2, exp) : 0; exp-- }
        newData = float32 * sign;
    }

    allData[j] = newData;
}

flow.set("Temperature", allData[0]);
flow.set("Humidity", allData[1]);
flow.set("LightIntensity", allData[2]);
flow.set("NH3PPM", allData[3]);
flow.set("NO2PPM", allData[4]);
flow.set("COPPM", allData[5]);
flow.set("CO2PPM", allData[6]);
flow.set("AtmosphericPressure", allData[7]);

return msg;

msg.payload={};

var SensorID = flow.get("SensorID");
var Time = flow.get("time");
var Temperature = flow.get("Temperature"); 
var Humidity = flow.get("Humidity");
var LightIntensity = flow.get("LightIntensity"); 
var NH3PPM = flow.get("NH3PPM");
var NO2PPM = flow.get("NO2PPM"); 
var COPPM = flow.get("COPPM"); 
var CO2PPM = flow.get("CO2PPM");
var AtmosphericPressure = flow.get("AtmosphericPressure");

msg.topic= `INSERT INTO Sensor_Data \
            ('SensorSensorID', 'TimeStamp', \
            'Temperature', 'Humidity', 'LightIntensity', \
            'NH3PPM', 'NO2PPM', 'COPPM', 'CO2PPM', \
            'AtmosphericPressure') \
            \
            VALUES \
            ('${SensorID}', '${Time}', ${Temperature}, \
            ${Humidity}, ${LightIntensity}, ${NH3PPM}, ${NO2PPM}, \
            ${COPPM}, ${CO2PPM}, ${AtmosphericPressure})`;
return msg;

var date = 0;

//getting difference between the 2 timestamps
time1 = Number('0x' + msg.payload.substr(7,4));
time2 = Number('0x' + msg.payload.substr(12,4));
var timeDif = time1 - time2;

//calculating difference in seconds
timeDif = Math.ceil(0.5 * timeDif);

//getting actual times
date = new Date();
date = date.getUTCFullYear() + '-' +
    ('00' + (date.getUTCMonth()+1)).slice(-2) + '-' +
    ('00' + date.getUTCDate()).slice(-2) + ' ' + 
    ('00' + date.getUTCHours()).slice(-2) + ':' + 
    ('00' + date.getUTCMinutes()).slice(-2) + ':' + 
    ('00' + (date.getUTCSeconds()- timeDif)).slice(-2);

flow.set("time", date);
return msg;
